import React from "react";
import { useState } from "react";

// Leaderboard page

function Leaderboard() {
    const [listOfPlayers, setListOfPlayers] = useState([]);
    const [column, setColumn] = useState([]);
    const [orderDirection, setOrderDirection] = useState('asc');
    
    const handleSubmit = async () => {
        try {
            const response = await fetch(`/query?column=${column}&orderDirection=${orderDirection}`);
            const data = await response.json();
            setListOfPlayers(data);
          } catch (error) {
            console.error('Error fetching data:', error);
          }
    }

    return (
      <div>
        <div>
          <select value={column} onChange={(e) =>  setColumn(e.target.value)}>
            <option value="column1">birthYear</option>
            <option value="column2">weight</option>
            <option value="column3">height</option>
          </select>

          <select
            value={orderDirection}
            onChange={(e) => setOrderDirection(e.target.value)}>
            <option value="asc">Ascending</option>
            <option value="desc">Descending</option>
          </select>

          <button onClick={handleSubmit}>Submit</button>
        </div>

        {listOfPlayers.map((player) => (
        <div className="player" key={player.playerID}>   
            {player.nameFirst} {player.nameLast} {player.column}
        </div>
      ))}
      </div>
    );

}

export default Leaderboard;

