import React from "react";
import { useEffect, useState } from "react";

// Leaderboard page

function Leaderboard() {
    const [listOfPlayers, setListOfPlayers] = useState([]);
    const [table, setTable] = useState('');
  const [columns, setColumns] = useState([]);
  const [conditions, setConditions] = useState({});
  const [orderColumn, setOrderColumn] = useState('');
  const [orderDirection, setOrderDirection] = useState('asc');
    useEffect(() => {
        // fetch always succeeds (besides network error), so need additional error checking
        const year = 2022;
        const minGames = 20;
        fetch(`http://localhost:3001/leaderboard?year=${year}&minGames=${minGames}`)
          .then((res) => {
            if (res.ok) {
              return res.json();
            } else {
              console.log("FETCH failed");
            }
          })
          .then((data) => {
            if (data.error) {
              console.log(data.error);
            } else {
              setListOfPlayers(data);
            }
          })
          .catch((error) => console.log("ERROR", error));
      }, []); 

      const handleSubmit = async () => {}

    return (
      <div>
        <div>
          <input
            type="text"
            value={table}
            onChange={(e) => setTable(e.target.value)}
            placeholder="Table Name"
          />
          <select
            multiple
            onChange={(e) =>
              setColumns(
                Array.from(e.target.selectedOptions, (option) => option.value)
              )
            }
          >
            <option value="column1">Column 1</option>
            <option value="column2">Column 2</option>
            <option value="column3">Column 3</option>
          </select>
          <input
            type="text"
            onChange={(e) =>
              setConditions({ ...conditions, conditionKey: e.target.value })
            }
            placeholder="Condition"
          />
          <input
            type="text"
            value={orderColumn}
            onChange={(e) => setOrderColumn(e.target.value)}
            placeholder="Order Column"
          />
          <select
            value={orderDirection}
            onChange={(e) => setOrderDirection(e.target.value)}
          >
            <option value="asc">Ascending</option>
            <option value="desc">Descending</option>
          </select>
          <button onClick={handleSubmit}>Submit</button>
        </div>
      </div>
    );

}

export default Leaderboard;

